import React from 'react';
import axios from "axios";
import Products from '../components/Products';
import Filter from '../components/Filter.js';


class ProductsList extends React.Component {
    constructor(props){
        super(props);

        this.handleProductsChange = this.handleProductsChange.bind(this);

        this.state = {
            products: [],
            brands: []
        }
    }

    handleProductsChange(products) {
        this.setState({products: products.results})
    }

    fetchProducts = () => {
        axios.get("http://127.0.0.1:8000/api/products").then(res => {
          this.setState({
            products: res.data.results
          });
        });
    }


    componentDidMount() {
        this.fetchProducts();
    }

    componentWillReceiveProps(newProps) {
    if (newProps.token) {
      this.fetchProducts();
    }
  }

    render() {
        return (
        <main id="main" class="main_items">
		<div class="main_content">
			<div class="item_list">
				<Filter onProductsChange={this.handleProductsChange}/>
				<div>
					<div class="sort">
						<span>Сортировать по:</span><span>
							<div class="select_container sort_type">
								<div class="sort_select" id="sort_select">
									<span class="cur_select_sort">возрастнию цены</span><img src="images/arrow_black.png"/>
								</div>
								<div class="select_list_sort">
									<div class="select_list_wrapper_sort">
										<div class="select_list_item_sort">
											<input type="radio" id="price_up" value="" name="sort_type" checked="checked"/>
											<label for="price_up">возрастанию цены</label>
										</div>
										<div class="select_list_item_sort">
											<input type="radio" id="price_down" value="" name="sort_type"/>
											<label for="price_down">убыванию цены</label>
										</div>
										<div class="select_list_item_sort">
											<input type="radio" id="name_sort" value="" name="sort_type"/>
											<label for="name_sort">названию</label>
										</div>
									</div>
								</div>
						</div>
						</span>
					</div>
				<div class="item_list_container">

					<Products data={this.state.products}/>
                    </div>
				</div>
			</div>
		</div>
	</main>
    )}
}

export default ProductsList
