import React from 'react';
import SizeSelectButton from './SizeSelectButton.js';
import AmountSelectButton from './AmountSelectButton.js';

class ProductInfoForm extends React.Component {

    constructor(props) {
        super(props);

        this.handleAmountChange = this.handleAmountChange.bind(this);
        this.handleSizeChange = this.handleSizeChange.bind(this);

        this.state = {
            amount: 1,
            size: null
        }
    }

    handleAmountChange = (amount) => {
        this.setState({amount: amount})
    }

    handleSizeChange = (size) => {
        this.setState({size: size})
    }

    render() {
        return (
            <form class="info_form">
                <SizeSelectButton handleSizeChange={this.handleSizeChange}/>
                <AmountSelectButton handleAmountChange={this.handleAmountChange}/>
                <div class="add_in_cart">
                    <button type="submit" name="add_in_cart_btn" class="add_in_cart_btn">Отправить</button>
                </div>
            </form>
        )
    }
}

export default ProductInfoForm;
