import React from 'react';


class SizeSelectButton extends React.Component {

    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div class="item_size item_info_list">
                <span>Размер:</span>

                <div class="radio_select " id="size_select">
                    <span class="cur_selected_size">{this.props.sizes ? this.props.sizes[0] : "Нет в наличии"}</span>
                </div>
                <div class="select_list">
                    <div class="select_list_wrapper">
                        {this.state.sizes && this.state.sizes.map(size => {
                            <label class="size_label">
                                <input type="radio" name="size" value={size} key={size}/>
                                <span>{size}</span>
                            </label>
                        })}
                    </div>
                </div>
            </div>
        )
    }
}

export default SizeSelectButton;
