import React from 'react';
import axios from 'axios';

import minus from '../images/minus.png';
import plus from '../images/plus.png';

import ProductInfoForm from './ProductInfoForm';

class ProductDetail extends React.Component {
    constructor(props) {
        super(props);
        this.fetchProduct = this.fetchProduct.bind(this);
        this.state = {
            product: null
        }
    }

    fetchProduct = () => {
        axios.get("http://127.0.0.1:8000/api/product/1").then(
            (res) => {{this.setState({product: res.data});
            console.log(this.state.product)}}
        )
    }

    componentDidMount() {
        this.fetchProduct();
    }

    render() {
        return (
            <main id="main" class="main_item">
                <div class="main_content">
                    <div class="item_descr">
                        <div class="item_title">
                            {this.state.product && this.state.product.name}
                        </div>
                        <div class="item_descr_main">
                            <div class="item_pics">
                                <div class="item_pic">
                                    <img src={this.state.product && "http://127.0.0.1:8000" + this.state.product.main_photo}/>
                                </div>
                            </div>
                            <div class="item_info">
                                <div class="item_price item_info_text">
                                    <span>{this.state.product && this.state.product.price}₽</span>
                                </div>
                                <div class="item_brand item_info_text">
                                    <span>Бренд: </span><span>{this.state.product && this.state.product.brand}</span>
                                </div>
                                <div class="item_season item_info_text">
                                    <span>Сезон: </span><span>{this.state.product && this.state.product.season}</span>
                                </div>
                                <ProductInfoForm/>
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        )
    }

}

export default ProductDetail;
