import React, {Suspense, useState} from 'react';
import axios from 'axios';

import ProductInfoForm from '../components/ProductInfoForm.js';
import {fetchProductData} from '../FetchProductApi.js';

const initialResource = fetchProductData();

function ProductDetail() {

    const [resource, setResource] = useState(initialResource);
    const [size, setSize] = useState(null);
    console.log(resource.product.read());
    return (
    <>
        <main id="main" class="main_item">
            <div class="main_content">
                <div class="item_descr">
                    <div class="item_title">
                        {resource.product.name}
                    </div>
                    <div class="item_descr_main">
                        <div class="item_pics">
                            <div class="item_pic">
                                <img src={resource.product.main_photo}/>
                            </div>
                        </div>
                        <div class="item_info">
                            <div class="item_price item_info_text">
                                <span>{resource.product.price}₽</span>
                            </div>
                            <div class="item_brand item_info_text">
                                <span>Бренд: </span><span>{resource.product.brand}</span>
                            </div>
                            <div class="item_season item_info_text">
                                <span>Сезон: </span><span>{resource.product.season}</span>
                            </div>
                            <Suspense fallback={"loading..."}>
                                <ProductInfoForm resource={resource}/>
                            </Suspense>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    </>
    )
}

export default ProductDetail;
