import React from 'react';
import axios from "axios";

import arrow from "../images/arrow_black.png";

class Filter extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            season: [],
            size: [],
            from: '',
            to: '',
            brand : [],
            season_clicked: false,
            size_clicked: false,
            price_clicked: false,
            brand_clicked: false,
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fetchBrands = this.fetchBrands.bind(this);
    }

    fetchBrands = () => {
        axios.get("http://127.0.0.1:8000/api/").then(res => {
            this.setState({
                brands: res.data.results
            });
        });
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        const query = this.state;
        axios.get("http://127.0.0.1:8000/api/filter", query).then(result =>
            console.log(result)
        )
    }

    componentDidMount() {
        this.fetchBrands();
    }



    render() {
        return (
            <div className="filters">
                <div className="filters_content">
                    <div className="filters_text">
                        <span>Фильтры</span>
                    </div>
                    <form onSubmit={this.handleSubmit}>
                        <div className={this.state.brand_clicked ? "filter_brand filter_block filter_opacity rotate_arrow" : "filter_brand filter_block"}
                                        onClick={(e) => {this.setState({brand_clicked: !this.state.brand_clicked})}}>

                            <div className="filter_title">
                                Бренд
                                <img src={arrow} className="arrow_filter"/>
                            </div>

                            <div className="filter_container filter_list brands_list disable">
                                {this.state.brand.map(brand => <input type="checkbox" name="brand" value={brand}/> )}
                            </div>

                        </div>
                        <div className={this.state.season_clicked ? "filter_season filter_block filter_opacity" : "filter_season filter_block"}
                                        onClick={(e) => {this.setState({season_clicked: !this.state.season_clicked})}}>
                            <div className="filter_title">
                                Сезон
                                <img src={arrow} className="arrow_filter"/>
                            </div>
                            <div className="filter_container filter_list season_list disable">
                                {[1,2,3,4,5].map( season =>
                            <label>
                                <input type="checkbox" name="season" value={season} onChange={this.onChange}/>
                                <span>Любой</span>
                            </label>
                        )}
                            </div>
                        </div>
                        <div className={this.state.size_clicked ? "filter_size filter_block filter_opacity" : "filter_size filter_block"}
                                        onClick={(e) => {this.setState({size_clicked: !this.state.size_clicked})}}>
                            <div className="filter_title">
                                Размер
                                <img src={arrow} className="arrow_filter"/>
                            </div>
                            <div className="filter_container filter_list size_list disable">
                                {['XS','S','M','L','XL','XXL'].map( size =>
                                <label>
                                    <input type="checkbox" name="size" value={size} onChange={this.onChange}/>
                                    <span>XS</span>
                                </label>
                                )}
                            </div>
                        </div>
                        <div className={this.state.price_clicked ? "filter_price filter_block filter_opacity" : "filter_price filter_block"}
                                        onClick={(e) => {this.setState({price_clicked: !this.state.price_clicked})}}>
                            <div className="filter_title">
                                Цена
                                <img src={arrow}   className="arrow_filter"/>
                            </div>
                                <div className={this.state.price_clicked ? "filter_container filter_block prise_gap filter_opacity disable" : "filter_container filter_block prise_gap disable"}
                                        onClick={(e) => {this.setState({price_clicked: !this.state.price_clicked})}}>
                                    <input type="text" name="from" placeholder="От" onChange={this.onChange}/>
                                    <input type="text" name="to" placeholder="До" onChange={this.onChange}/>
                                </div>
                            <div className="filter_apply filters_text">
                                <button type="submit" className="apply_filters_btn">Применить</button>
                            </div>
                        </div>
                    </form>
                </div>
			</div>
        )
    }
}


export default Filter;
