import React from 'react';
import axios from 'axios';

import minus from '../images/minus.png';
import plus from '../images/plus.png';

class ProductDetail extends React.Component {
    constructor(props) {
        super(props);
        this.fetchProduct = this.fetchProduct.bind(this);
        this.state = {
            product: null
        }

    }

    fetchProduct = () => {
        axios.get("http://127.0.0.1:8000/api/product/1").then(
            (res) => {{this.setState({product: res.data});
            console.log(this.state.product)}}
        )
    }

    componentDidMount() {
        this.fetchProduct();
    }

    render() {
        return (
            <main id="main" class="main_item">
                <div class="main_content">
                    <div class="item_descr">
                        <div class="item_title">
                            {this.state.product && this.state.product.name}
                        </div>
                        <div class="item_descr_main">
                            <div class="item_pics">
                                <div class="item_pic">
                                    <img src={"http://127.0.0.1:8000/" + this.state.product && this.state.product.main_photo}/>
                                </div>
                            </div>
                            <div class="item_info">
                                <div class="item_price item_info_text">
                                    <span>{this.state.product && this.state.product.price}₽</span>
                                </div>
                                <div class="item_brand item_info_text">
                                    <span>Бренд: </span><span>{this.state.product && this.state.product.brand}</span>
                                </div>
                                <div class="item_season item_info_text">
                                    <span>Сезон: </span><span>{this.state.product && this.state.product.season}</span>
                                </div>
                                <form class="info_form">
                                    <div class="item_size item_info_list">
                                        <span>Размер:</span>

                                        <div class="radio_select " id="size_select">
                                            <span class="cur_selected_size">{this.state.product && this.state.product.sizes ? this.state.product.sizes[0] : "Нет в наличии"}</span>
                                        </div>
                                        <div class="select_list">
                                            <div class="select_list_wrapper">
                                                {this.state.product && this.state.product.sizes.map(size => {
                                                <label class="size_label">
                                                    <input type="radio" name="size" id="size1" value="size"/>
                                                    <span>{size}</span>
                                                </label>

                                                })}


                                            </div>
                                        </div>
                                    </div>
                                    <div class="count_select_block">
                                        <span>Количество:</span>
                                        <div class="count_select">
                                            <button type="button" class="count_minus count_char" onclick="countChange(this)"><img src={minus}/></button><input type="number" class="cur_selected_count" name="count" id="count" value="1" min="0" max="30"/><button type="button" class="count_plus count_char" onclick="countChange(this)"><img src={plus}/></button>
                                        </div>
                                    </div>
                                    <div class="add_in_cart">
                                        <button type="submit" name="add_in_cart_btn" class="add_in_cart_btn">Отправить</button>
                                    </div>
                                </form>

                            </div>
                        </div>
                    </div>
                </div>
            </main>
        )
    }

}

export default ProductDetail;
