import React from 'react';
import axios from "axios";
import Products from '../components/Products';
import Filter from '../components/Filter.js';

import arrow from "../images/arrow_black.png";

class ProductsList extends React.Component {
    constructor(props){
        super(props);

        this.handleProductsChange = this.handleProductsChange.bind(this);

        this.state = {
            products: [],
            brands: []
        }
    }

    handleProductsChange(products) {
        this.setState({products: products.results})
    }

    fetchProducts = () => {
        axios.get("http://127.0.0.1:8000/api/products").then(res => {
          this.setState({
            products: res.data.results
          });
        });
    }


    componentDidMount() {
        this.fetchProducts();
    }

    componentWillReceiveProps(newProps) {
    if (newProps.token) {
      this.fetchProducts();
    }
  }

    render() {
        return (
        <main id="main" class="main_items">
		<div class="main_content">
			<div class="item_list">
				<Filter onProductsChange={this.handleProductsChange} arrow={arrow}/>
				<div>

				<div class="item_list_container">

					<Products data={this.state.products}/>
                    </div>
				</div>
			</div>
		</div>
	</main>
    )}
}

export default ProductsList
