import React, {Suspense} from 'react';
import axios from 'axios';

const ProductInfoForm = React.lazy(() => import('../components/ProductInfoForm.js'));

class ProductDetail extends React.Component {
    constructor(props) {
        super(props);
        this.fetchProduct = this.fetchProduct.bind(this);
        this.state = {
            product: null,
            size: null
        }
    }

    fetchProduct = () => {
        axios.get("http://127.0.0.1:8000/api/product/" + this.props.match.params.product_id).then(
            (res) => {{this.setState({product: res.data, size: res.data.size});

            console.log(this.state.product)}}
        )
    }

    componentDidMount() {
        this.fetchProduct();
    }



    render() {
        return (

            <main id="main" class="main_item">
                <div class="main_content">
                    <div class="item_descr">
                        <div class="item_title">
                            {this.state.product && this.state.product.name}
                        </div>
                        <div class="item_descr_main">
                            <div class="item_pics">
                                <div class="item_pic">
                                    <img src={this.state.product && "http://127.0.0.1:8000" + this.state.product.main_photo}/>
                                </div>
                            </div>
                            <div class="item_info">
                                <div class="item_price item_info_text">
                                    <span>{this.state.product && this.state.product.price}₽</span>
                                </div>
                                <div class="item_brand item_info_text">
                                    <span>Бренд: </span><span>{this.state.product && this.state.product.brand}</span>
                                </div>
                                <div class="item_season item_info_text">
                                    <span>Сезон: </span><span>{this.state.product && this.state.product.season}</span>
                                </div>
                                <Suspense fallback={() => console.log("prikol")}>
                                    <ProductInfoForm pk={this.state.product && this.state.product.pk} size={this.state.size} sizes={this.state.product && this.state.product.sizes}/>
                                </Suspense>
                            </div>
                        </div>
                    </div>
                </div>
            </main>

        )
    }

}

export default ProductDetail;
