import React, {useState} from 'react';
import axios from "axios";
import Filter from '../components/Filter.js';
import Sorter from '../components/Sorter.js';

import arrow from "../images/arrow_black.png";

import fetchProductsData from "../FetchProductApi.js"


const initialResource = fetchProductsData();

function ProductsList(props) {

    const [resource, setResource] = useState(initialResource);

    return (
        <main id="main" class="main_items">
            <div class="main_content">
                <div class="item_list">
                    <Filter onProductsChange={setResource} arrow={arrow}/>
                    <div>
                        <Sorter onProductsChange={setResource} resource={resource} arrow={arrow} />
                        <div class="item_list_container">
                            <Products data={this.state.products}/>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    )
}

export default ProductsList
