import axios from 'axios';


const fetchProduct = () => {
    return axios.get("http://127.0.0.1:8000/api/product/1")
}

const fetchProducts = () => {
    return axios.get("http://127.0.0.1:8000/api/products")
}

function wrapPromise(promise) {
  let status = "pending";
  let result;
  let suspender = promise.then(
    r => {
      status = "success";
      result = r;
    },
    e => {
      status = "error";
      result = e;
    }
  );

  return {
    read() {
      if (status === "pending") {
        throw suspender;
      } else if (status === "error") {
        throw result;
      }
      return result;
    }
  };
};

export function fetchProductsData(data=null) {
    if(!data){
        let productPromise = fetchProducts();
    }
    let productPromise = data;

    return {
        products: wrapPromise(productPromise)
    };
};

export function fetchProductData() {
    let productPromise = fetchProduct();

    return {
        product: wrapPromise(productPromise)
    };
};
