import React from 'react';
import SizeSelectButton from './SizeSelectButton.js';
import AmountSelectButton from './AmountSelectButton.js';

class ProductInfoForm extends React.Component {

    constructor(props) {
        super(props);

        this.handleAmountChange = this.handleAmountChange.bind(this);
        this.handleSizeChange = this.handleSizeChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            amount: 1,
            size: null,
        }
    }

    handleAmountChange = (amount) => {
        this.setState({amount: amount})
    }

    handleSizeChange = (event) => {
        this.setState({size: event.target.value})
    }

    onSubmit = (event) => {
        if (this.state.size === null){
            this.setState({size: this.props.sizes[0]})
        }
        event.preventDefault();

        console.log(this.state.amount, this.state.size,this.props.pk);

    }

    render() {
        return (
            <form class="info_form" onSubmit={this.onSubmit}>
                <SizeSelectButton handleSizeChange={this.handleSizeChange} size={this.state.size} sizes={this.props.sizes}/>
                <AmountSelectButton handleAmountChange={this.handleAmountChange} amount={this.state.amount}/>
                <div class="add_in_cart">
                    <button type="submit" name="add_in_cart_btn" class="add_in_cart_btn">Отправить</button>
                </div>
            </form>
        )
    }
}

export default ProductInfoForm;
