import React from 'react';
import axios from "axios";


class Filter extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            season: [],
            size: [],
            from: '',
            to: '',
            brand : [],
            season_clicked: false,
            size_clicked: false,
            price_clicked: false,
            brand_clicked: false,
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fetchBrands = this.fetchBrands.bind(this);
        this.arrow = this.props.arrow
    }

    fetchBrands = () => {
        axios.get("http://127.0.0.1:8000/api/brands").then(res => {
            this.setState({
                brands: res.data.results
            });
        });
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        axios.get("http://127.0.0.1:8000/api/filter?brand=" + this.state.brand + "&season=" + this.state.season +
        "&size=" + this.state.size + "&from=" + this.state.from + "&to=" + this.state.to).then(result =>
            this.props.onProductsChange(result.data.results)
        )
    }

    componentDidMount() {
        this.fetchBrands();
    }

    render() {
        return (
            <div className="filters">
                <div className="filters_content">
                    <div className="filters_text">
                        <span>Фильтры</span>
                    </div>
                    <form onSubmit={this.handleSubmit}>
                        <div className="filter_brand filter_block"
                                        onClick={(e) => {this.setState({brand_clicked: !this.state.brand_clicked})}}>

                            <div className="filter_title">
                                Бренд
                                <img src={this.arrow} className={this.state.brand_clicked ? "arrow_filter rotate_arrow" : "arrow_filter" }/>
                            </div>

                            <div className={this.state.brand_clicked ? "filter_container filter_opacity " : "filter_container"}>
                                {this.state.brand.map(brand => <input type="checkbox" name="brand" value={brand}/> )}
                            </div>

                        </div>
                        <div className="filter_season filter_block"
                                        onClick={(e) => {this.setState({season_clicked: !this.state.season_clicked})}}>
                            <div className="filter_title">
                                Сезон
                                <img src={this.arrow} className="arrow_filter" className={this.state.season_clicked ? "arrow_filter rotate_arrow" : "arrow_filter" }/>
                            </div>
                            <div className={this.state.season_clicked ? "filter_container filter_list season_list filter_slide_toggle" : "filter_container filter_list season_list "}>
                                {[[2, "Лето"],[3, "Зима"],[4, "Осень"],[5, "Весна"]].map( season =>
                            <label>
                                <input type="checkbox" name="season" value={season[0]} onChange={this.onChange}/>
                                <span>{season[1]}</span>
                            </label>
                        )}
                            </div>
                        </div>
                        <div className="filter_size filter_block"
                                        onClick={(e) => {this.setState({size_clicked: !this.state.size_clicked})}}>
                            <div className="filter_title">
                                Размер
                                <img src={this.arrow} className="arrow_filter" className={this.state.size_clicked ? "arrow_filter rotate_arrow" : "arrow_filter" }/>
                            </div>
                            <div className={this.state.size_clicked ? "filter_container filter_list size_list filter_slide_toggle" : "filter_container filter_list size_list"}>
                                {['XS','S','M','L','XL','XXL'].map( size =>
                                <label>
                                    <input type="checkbox" name="size" value={size} onChange={this.onChange}/>
                                    <span>{size}</span>
                                </label>
                                )}
                            </div>
                        </div>
                        <div className="filter_price filter_block"
                                        onClick={(e) => {this.setState({price_clicked: !this.state.price_clicked})}}>
                            <div className="filter_title">
                                Цена
                                <img src={this.arrow}   className="arrow_filter"/>
                            </div>
                                <div className={this.state.price_clicked ? "filter_container filter_block prise_gap filter_slide_toggle" : "filter_container filter_block prise_gap"}
                                        onClick={(e) => {this.setState({price_clicked: !this.state.price_clicked})}}>
                                    <input type="text" name="from" placeholder="От" onChange={this.onChange}/>
                                    <input type="text" name="to" placeholder="До" onChange={this.onChange}/>
                                </div>
                            <div className="filter_apply filters_text">
                                <button type="submit" className="apply_filters_btn">Применить</button>
                            </div>
                        </div>
                    </form>
                </div>
			</div>
        )
    }
}


export default Filter;
