import React from 'react';


function OrderTitle(props) {
    if (props.order === 'up'){
            return <span className="cur_select_sort">возрастанию цены</span>;
        }
        else if (props.order === 'down'){
            return <span className="cur_select_sort">убыванию цены</span>;
        }
        else if (props.order === 'name'){
            return <span className="cur_select_sort">названию</span>;
        }
}

class Sorter extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            isOpen: false,
            order: 'up'
        };
        this.arrow = this.props.arrow;
    }

    onChange = (e,value) => {
        this.setState({order: value});

    }

    upCompare = (a,b) => {
        if (a.price < b.price) {return -1}
        if (a.price > b.price) {return 1}
        return 0
    }

    downCompare = (a,b) => {
        if (a.price < b.price) {return 1}
        if (a.price > b.price) {return -1}
        return 0
    }

    popularCompare = (a,b) => {
        if (a.sold < b.sold) {return 1}
        if (a.sold > b.sold) {return -1}
        return 0
    }

    sortProducts = (products) => {
        if (this.order === 'up') {
            products.sort(this.upCompare)
        }
        else if (this.order === 'down'){
            products.sort(this.downCompare)
        }
        else if (this.order === 'popular'){
            products.sort(this.popularCompare)
        }
    }

    render() {
        return (
            <div class="sort">
                <span>Сортировать по:</span>
                <span>
                    <div class="select_container sort_type">
                        <div class="sort_select" id="sort_select"
                        onClick={(e) => {this.setState({isOpen: !this.state.isOpen});}}>
                            <OrderTitle order={this.state.order} />
                            <img src={this.arrow} className={this.state.isOpen ? 'rotate_arrow' : ''}/>
                        </div>
                        <div class={this.state.isOpen ? "select_list_sort sort_list-open" : "select_list_sort"}>
                            <div class="select_list_wrapper_sort">
                                <div class="select_list_item_sort" onClick={(e) => {
                                this.setState({order: 'up', isOpen: false});
                                console.log(this.props.data);
                                this.props.onProductsChange(this.props.products)}}>
                                    <input type="radio" id="price_up" value="" name="sort_type" checked={this.state.order === 'up' ? 'checked' : ''} />
                                    <label for="price_up">возрастанию цены</label>
                                </div>
                                <div class="select_list_item_sort" onClick={(e) => this.setState({order: 'down', isOpen: false})}>
                                    <input type="radio" id="price_down" value="" name="sort_type" checked={this.state.order === 'down' ? 'checked' : ''} />
                                    <label for="price_down">убыванию цены</label>
                                </div>
                                <div class="select_list_item_sort" onClick={(e) => this.setState({order: 'name', isOpen: false})}>
                                    <input type="radio" id="name_sort" value="" name="sort_type"checked={this.state.order === 'name' ? 'checked' : ''} />
                                    <label for="name_sort">названию</label>
                                </div>
                            </div>
                        </div>
                    </div>
                </span>
            </div>
        )
    }
}

export default Sorter;
