import React from 'react';
import SizeSelectButton from './SizeSelectButton.js';
import AmountSelectButton from './AmountSelectButton.js';

class ProductInfoForm extends React.Component {
    render() {
        return (
            <form class="info_form">
                <div class="add_in_cart">
                    <SizeSelectButton/>
                    <AmountSelectButton/>
                    <button type="submit" name="add_in_cart_btn" class="add_in_cart_btn">Отправить</button>
                </div>
            </form>
            )
    }
}

export default ProductInfoForm;
